$(document).ready(function(){$('#submit').click(function(){var contact_name=$('#contact_name').val();var contact_email=$('#contact_email').val();var contact_subject=$('#contact_subject').val();var contact_message=$('#contact_message').val();var reCaptchaResp=grecaptcha.getResponse();if((contact_name!='')&&(contact_email!='')&&(contact_message!='')){$.ajax({type:"post",url:BASE_URL+"enquiry/contactUs",data:{contact_name:contact_name,contact_subject:contact_subject,contact_email:contact_email,contact_message:contact_message,"g-recaptcha-response":reCaptchaResp},success:function(data){if(data=="errorgoogle"){$('#error').html("Google Captch not Varified");$('#error').css('display','block').fadeOut(50000);}else{$('#success').css('display','block').fadeOut(50000);}$('#contact_name').val('');$('#contact_email').val('');$('#contact_subject').val('');$('#contact_message').val('');}});}else{$('#error').css('display','block').fadeOut(50000);}return false;});});